cookies:
@{
        parsedCookies = [:]
        for (cookie in cookies) {
            parsedCookies[cookie.name] = cookie.value.toString()
        }
        if (!parsedCookies) return null
        return parsedCookies
    }

create_date:
@{
        createDate = dateFormat.format(model?.creationtime ?: new Date())
        return createDate
    }
